import React, { useState, useEffect } from "react";

function App() {
  const [status, setStatus] = useState({
    running: false,
    start_time: null,
    duration_seconds: 0,
  });

  // Fetch status from backend
  const fetchStatus = async () => {
    try {
      const response = await fetch("http://127.0.0.1:3001/status");
      const data = await response.json();
      setStatus(data);
    } catch (err) {
      console.error("Failed to fetch status:", err);
    }
  };

  // Start session
  const startSession = async () => {
    await fetch("http://127.0.0.1:3001/start", { method: "POST" });
    fetchStatus();
  };

  // Stop session
  const stopSession = async () => {
    await fetch("http://127.0.0.1:3001/stop", { method: "POST" });
    fetchStatus();
  };

  // Poll status every second
  useEffect(() => {
    fetchStatus();
    const interval = setInterval(fetchStatus, 1000);
    return () => clearInterval(interval);
  }, []);

  return (
    <div style={{ textAlign: "center", marginTop: "50px" }}>
      <h1>Time Tracker</h1>
      <div>
        <p>Running: {status.running ? "Yes" : "No"}</p>
        <p>
          Start Time: {status.start_time ? new Date(status.start_time).toLocaleString() : "-"}
        </p>
        <p>Duration: {status.duration_seconds ?? 0} seconds</p>
      </div>
      <button onClick={startSession} style={{ marginRight: "10px" }}>Start</button>
      <button onClick={stopSession}>Stop</button>
    </div>
  );
}

export default App;
