import React, { useState, useEffect } from "react";

function App() {
  const [status, setStatus] = useState(null);

  // Fetch status from backend
  const fetchStatus = async () => {
    const response = await fetch("http://127.0.0.1:3000/status");
    const data = await response.json();
    setStatus(data);
  };

  // Start session
  const startSession = async () => {
    await fetch("http://127.0.0.1:3000/start", { method: "POST" });
    fetchStatus();
  };

  // Stop session
  const stopSession = async () => {
    await fetch("http://127.0.0.1:3000/stop", { method: "POST" });
    fetchStatus();
  };

  // Poll status every 1 second (optional)
  useEffect(() => {
    fetchStatus();
    const interval = setInterval(fetchStatus, 1000);
    return () => clearInterval(interval);
  }, []);

  return (
    <div style={{ textAlign: "center", marginTop: "50px" }}>
      <h1>Time Tracker</h1>
      {status && (
        <div>
          <p>Running: {status.running ? "Yes" : "No"}</p>
          <p>
            Start Time:{" "}
            {status.start_time ? new Date(status.start_time).toLocaleString() : "-"}
          </p>
          <p>Duration: {status.duration_seconds ?? 0} seconds</p>
        </div>
      )}
      <button onClick={startSession} style={{ marginRight: "10px" }}>
        Start
      </button>
      <button onClick={stopSession}>Stop</button>
    </div>
  );
}

export default App;
