import React, { useState, useEffect } from "react";

function App() {
  const [status, setStatus] = useState({
    running: false,
    start_time: null,
    duration_seconds: 0,
  });

  const [sessions, setSessions] = useState([]);

  // Load initial status from backend
  useEffect(() => {
    fetch("http://127.0.0.1:3001/status")
      .then(res => res.json())
      .then(data => setStatus(data));
  }, []);

  // Live timer that updates every second
  useEffect(() => {
    let interval = null;

    if (status.running && status.start_time) {
      interval = setInterval(() => {
        const start = new Date(status.start_time);
        const now = new Date();
        const diffSeconds = Math.floor((now - start) / 1000);

        setStatus(prev => ({
          ...prev,
          duration_seconds: diffSeconds,
        }));
      }, 1000);
    }

    return () => clearInterval(interval);
  }, [status.running, status.start_time]);

  // Start session
  const startSession = async () => {
    const res = await fetch("http://127.0.0.1:3001/start", { method: "POST" });
    const data = await res.json();
    setStatus(data);
  };

  // Stop session
  const stopSession = async () => {
    const res = await fetch("http://127.0.0.1:3001/stop", { method: "POST" });
    const data = await res.json();

    if (data.duration_seconds != null) {
      setSessions(prev => [...prev, {
        start: data.start_time,
        end: data.end_time,
        duration: data.duration_seconds
      }]);
    }

    // Reset the UI tracker
    setStatus({
      running: false,
      start_time: null,
      duration_seconds: 0
    });
  };

  // Delete a session by index on the frontend only
  const deleteSession = (index) => {
    setSessions(prev => prev.filter((_, i) => i !== index));
  };

  return (
    <div style={{ textAlign: "center", marginTop: "50px" }}>
      <h1>Time Tracker</h1>

      <p>Running: {status.running ? "Yes" : "No"}</p>

      <p>
        Start Time:{" "}
        {status.start_time
          ? new Date(status.start_time).toLocaleString()
          : "-"}
      </p>

      <p>
        Duration: <b>{status.duration_seconds}</b> seconds
      </p>

      <button onClick={startSession} style={{ marginRight: "10px" }}>
        Start
      </button>

      <button onClick={stopSession}>Stop</button>

      <hr />

      <h2>Completed Sessions</h2>
      <ul>
        {sessions.map((s, idx) => {
          const start = new Date(s.start);
          const end = new Date(s.end);
          const day = start.toLocaleDateString(undefined, { weekday: "long" });
          const date = start.toLocaleDateString();

          return (
          <li key={idx}>
            <b>Session {idx + 1}</b><br />
            Date: {date} ({day}) <br />
            Start: {start.toLocaleTimeString()} <br />
            End: {end.toLocaleTimeString()} <br />
            Duration: <b>{s.duration}</b> seconds
            <button 
              onClick={() => deleteSession(idx)} 
            >
              Delete
            </button>
          </li>
          );
        })}
      </ul>

    </div>
  );
}

export default App;
