import React, { useState, useEffect } from "react";

function App() {
  const [status, setStatus] = useState({
    running: false,
    start_time: null,
    duration_seconds: 0,
  });

  const [sessions, setSessions] = useState([]); 
  // <-- store all completed session durations

  // Fetch initial state when the app loads
  useEffect(() => {
    fetch("http://127.0.0.1:3001/status")
      .then(res => res.json())
      .then(data => setStatus(data));
  }, []);

  // Start session
  const startSession = async () => {
    const res = await fetch("http://127.0.0.1:3001/start", { method: "POST" });
    const data = await res.json();
    setStatus(data);
  };

  // Stop session: update duration AND add to history
  const stopSession = async () => {
    const res = await fetch("http://127.0.0.1:3001/stop", { method: "POST" });
    const data = await res.json();

    // If backend returns a duration, push it into the session list
    if (data.duration_seconds != null) {
      setSessions(prev => [...prev, data.duration_seconds]);
    }

    setStatus(data);
  };

  return (
    <div style={{ textAlign: "center", marginTop: "50px" }}>
      <h1>Time Tracker</h1>

      <p>Running: {status.running ? "Yes" : "No"}</p>

      <p>
        Start Time:{" "}
        {status.start_time
          ? new Date(status.start_time).toLocaleString()
          : "-"}
      </p>

      <p>
        Duration: <b>{status.duration_seconds ?? 0}</b> seconds
      </p>

      <button onClick={startSession} style={{ marginRight: "10px" }}>
        Start
      </button>

      <button onClick={stopSession}>Stop</button>

      <hr />

      <h2>Completed Sessions</h2>
      <ul>
        {sessions.map((dur, idx) => (
          <li key={idx}>
            Session {idx + 1}: <b>{dur}</b> seconds
          </li>
        ))}
      </ul>
    </div>
  );
}

export default App;
