import React, { useState, useEffect } from "react";

function App() {
  const [status, setStatus] = useState({
    running: false,
    start_time: null,
    duration_seconds: 0,
  });

  const [liveDuration, setLiveDuration] = useState(0);

  // --- Fetch initial state ---
  useEffect(() => {
    fetch("http://127.0.0.1:3001/status")
      .then(res => res.json())
      .then(data => {
        setStatus(data);
        if (data.running && data.start_time) {
          const start = new Date(data.start_time).getTime();
          setLiveDuration((Date.now() - start) / 1000);
        }
      });
  }, []);

  // --- LIVE TIMER while running ---
  useEffect(() => {
    if (!status.running || !status.start_time) return;

    const startMs = new Date(status.start_time).getTime();

    const interval = setInterval(() => {
      const now = Date.now();
      const seconds = Math.floor((now - startMs) / 1000);
      setLiveDuration(seconds);
    }, 1000);

    return () => clearInterval(interval);
  }, [status.running, status.start_time]);

  // Start session
  const startSession = async () => {
    const res = await fetch("http://127.0.0.1:3001/start", { method: "POST" });
    const data = await res.json();

    setStatus(data);

    if (data.start_time) {
      setLiveDuration(0); // reset timer for new session
    }
  };

  // Stop session
  const stopSession = async () => {
    const res = await fetch("http://127.0.0.1:3001/stop", { method: "POST" });
    const data = await res.json();

    setStatus(data);

    // When stopped, use backend-provided final duration
    if (data.duration_seconds != null) {
      setLiveDuration(data.duration_seconds);
    }
  };

  return (
    <div style={{ textAlign: "center", marginTop: "50px" }}>
      <h1>Time Tracker</h1>

      <p>Running: {status.running ? "Yes" : "No"}</p>

      <p>
        Start Time:{" "}
        {status.start_time
          ? new Date(status.start_time).toLocaleString()
          : "-"}
      </p>

      <p>
        Duration: <b>{Math.floor(liveDuration)}</b> seconds
      </p>

      <button onClick={startSession} style={{ marginRight: "10px" }}>
        Start
      </button>

      <button onClick={stopSession}>Stop</button>
    </div>
  );
}

export default App;
