import React, { useState, useEffect } from "react";
import { io } from "socket.io-client";

function App() {
  const [status, setStatus] = useState({
    running: false,
    start_time: null,
    duration_seconds: 0,
  });

  useEffect(() => {
    // Connect to Node.js Socket.IO server
    const socket = io("http://localhost:3002");

    // Listen for events emitted by Rust backend
    socket.on("session_started", () => {
      setStatus(prev => ({ ...prev, running: true }));
    });

    socket.on("session_stopped", (duration) => {
      setStatus(prev => ({
        ...prev,
        running: false,
        duration_seconds: duration,
        start_time: null
      }));
    });

    return () => {
      socket.disconnect();
    };
  }, []);

  const startSession = async () => {
    await fetch("http://127.0.0.1:3001/start", { method: "POST" });
  };

  const stopSession = async () => {
    await fetch("http://127.0.0.1:3001/stop", { method: "POST" });
  };

  return (
    <div style={{ textAlign: "center", marginTop: "50px" }}>
      <h1>Time Tracker</h1>
      <div>
        <p>Running: {status.running ? "Yes" : "No"}</p>
        <p>
          Start Time: {status.start_time ? new Date(status.start_time).toLocaleString() : "-"}
        </p>
        <p>Duration: {status.duration_seconds ?? 0} seconds</p>
      </div>
      <button onClick={startSession} style={{ marginRight: "10px" }}>Start</button>
      <button onClick={stopSession}>Stop</button>
    </div>
  );
}

export default App;
